toolUseResult.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 220 30% 98%;
    --foreground: 220 20% 10%;
    --card: 0 0% 100%;
    --card-foreground: 220 20% 10%;
    --popover: 0 0% 100%;
    --popover-foreground: 220 20% 10%;
    --primary: 220 90% 56%;
    --primary-foreground: 0 0% 100%;
    --secondary: 280 70% 60%;
    --secondary-foreground: 0 0% 100%;
    --muted: 220 20% 94%;
    --muted-foreground: 220 10% 45%;
    --accent: 330 80% 65%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 98%;
    --border: 220 20% 90%;
    --input: 220 20% 90%;
    --ring: 220 90% 56%;
    --radius: 0.75rem;

    /* Custom tokens */
    --gradient-primary: linear-gradient(135deg, hsl(220 90% 56%) 0%, hsl(280 70% 60%) 50%, hsl(330 80% 65%) 100%);
    --gradient-card: linear-gradient(145deg, hsl(0 0% 100% / 0.9) 0%, hsl(220 30% 98% / 0.8) 100%);
    --gradient-hero: linear-gradient(180deg, hsl(220 30% 98%) 0%, hsl(240 30% 96%) 100%);
    --shadow-glow: 0 0 60px hsl(220 90% 56% / 0.2);
    --shadow-card: 0 8px 32px hsl(220 20% 10% / 0.08);
    --shadow-elevated: 0 20px 60px hsl(220 20% 10% / 0.12);
  }

  .dark {
    --background: 220 25% 8%;
    --foreground: 220 10% 95%;
    --card: 220 25% 12%;
    --card-foreground: 220 10% 95%;
    --popover: 220 25% 12%;
    --popover-foreground: 220 10% 95%;
    --primary: 220 90% 60%;
    --primary-foreground: 0 0% 100%;
    --secondary: 280 70% 65%;
    --secondary-foreground: 0 0% 100%;
    --muted: 220 20% 18%;
    --muted-foreground: 220 10% 60%;
    --accent: 330 80% 65%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 62% 50%;
    --destructive-foreground: 0 0% 98%;
    --border: 220 20% 20%;
    --input: 220 20% 20%;
    --ring: 220 90% 60%;

    --gradient-primary: linear-gradient(135deg, hsl(220 90% 60%) 0%, hsl(280 70% 65%) 50%, hsl(330 80% 65%) 100%);
    --gradient-card: linear-gradient(145deg, hsl(220 25% 14% / 0.9) 0%, hsl(220 25% 10% / 0.8) 100%);
    --gradient-hero: linear-gradient(180deg, hsl(220 25% 8%) 0%, hsl(240 25% 6%) 100%);
    --shadow-glow: 0 0 60px hsl(220 90% 60% / 0.15);
    --shadow-card: 0 8px 32px hsl(0 0% 0% / 0.3);
    --shadow-elevated: 0 20px 60px hsl(0 0% 0% / 0.4);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground antialiased;
    background: var(--gradient-hero);
    min-height: 100vh;
  }
}

@layer utilities {
  .gradient-text {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .glass-card {
    background: var(--gradient-card);
    backdrop-filter: blur(20px);
    border: 1px solid hsl(var(--border) / 0.5);
    box-shadow: var(--shadow-card);
  }

  .glow-effect {
    box-shadow: var(--shadow-glow);
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse 4s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
  }

  .upload-zone {
    background: linear-gradient(145deg, hsl(var(--muted) / 0.5), hsl(var(--background) / 0.8));
    border: 2px dashed hsl(var(--border));
    transition: all 0.3s ease;
  }

  .upload-zone:hover {
    border-color: hsl(var(--primary));
    background: linear-gradient(145deg, hsl(var(--primary) / 0.05), hsl(var(--background) / 0.9));
  }

  .style-card {
    background: var(--gradient-card);
    border: 2px solid transparent;
    transition: all 0.3s ease;
  }

  .style-card:hover {
    border-color: hsl(var(--primary) / 0.5);
    transform: translateY(-4px);
    box-shadow: var(--shadow-elevated);
  }

  .style-card.selected {
    border-color: hsl(var(--primary));
    box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
  }

  .shimmer {
    background: linear-gradient(
      90deg,
      hsl(var(--muted)) 0%,
      hsl(var(--muted-foreground) / 0.1) 50%,
      hsl(var(--muted)) 100%
    );
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }

  @keyframes shimmer {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
  }
}

