import { GenerateButton } from "@/components/GenerateButton";

// 示例生成结果图片
const sampleResults: Record<string, string> = {
  casual: "https://images.unsplash.com/photo-1515886657613-9f3515b0c78f?w=400&h=600&fit=crop",
